  return pos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
in vec4 color;
in vec2 uv0;
layout(std140) uniform Constant {
  float holeShape;
  float radius;
  float round;
  float opacity;
  vec2 center;
  vec2 rectSize;
  float aspectRatio;
};
float sdCircle(in vec2 p,float radius)
{
  return length(p) - radius;
}
float sdHexagonRadius( in vec2 p, in float r,float cornerRadius )
{
  r = r - cornerRadius;
  const vec3 k = vec3(-0.866025404,0.5,0.577350269);
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y)-cornerRadius;
}
float sdBoxRadius( in vec2 p, in vec2 b,float cornerRadius )
{
  b.x = b.x - cornerRadius;
  b.y = b.y - cornerRadius;
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0) - cornerRadius;
}
vec4 frag () {
  vec4 col = vec4(0.0,0.0,0.0,0.0);
  vec2 _uv = vec2(uv0.x,uv0.y*aspectRatio);
  vec2 _ct = vec2(center.x,center.y*aspectRatio);
  float _a = opacity;
  if(abs(float(holeShape)-0.0)<0.5){
    col.a = _a;
    return col;
  }else if(abs(float(holeShape)-2.0)<0.5){
    vec2 _size = vec2(radius/1.414,radius/1.414);
    if(abs(rectSize.x)>0.01){
      _size.x = rectSize.x*0.5;
      _size.y = rectSize.y*0.5;
    }
    float d = sdBoxRadius(_uv-_ct,_size,round);
    _a *= smoothstep(-0.01, 0.01,d);
  }else if(abs(float(holeShape)-3.0)<0.5){
    float d = sdHexagonRadius(_uv-_ct,radius/2.0*1.732,round);
    _a *= smoothstep(-0.01, 0.01,d);
  }else{
    float d = sdCircle(_uv-_ct,radius);
    _a *= smoothstep(-0.01, 0.01,d);
  }
  col.a = _a;
  return col;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

